class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ones = 0;
        int i = 0, ans = 0;
        char prev = '#';
        while(i < n) {
            if(s[i] == '1') {
                if(prev == '0') ans += ones;
                ones ++;
            }
            prev = s[i];
            i ++;
        }
        if(prev == '0') ans += ones;
        return ans;
    }
};

