class Solution {
    public:
    int maxOperations(string s) {
        int maxOps = 0;   
        int onesCount = 0;   
        vector<char> ops;   

        for (char c : s) {
            if (c == '1') {
                onesCount++;   
                ops.push_back(c);   
            } else if (c == '0' && !ops.empty() && ops.back() == '1') {
                maxOps += onesCount;   
                ops.push_back(c);   
            }
        }
        return maxOps;
    }
};
