class Solution {
public:
    int maxOperations(string s) {
        int cnt_1=0, cnt_0=0, ans=0;
        int i=0;
        for(auto ch:s){
            if(ch=='1'){
                ans+= cnt_1*cnt_0;
                cnt_1++;
                cnt_0=0;
            }
            else{
                cnt_0=1;
            }
        }
        ans+= cnt_1*cnt_0;
        return ans;
    }
};
