import re

def compress_zeros(input_string):
    return re.sub(r'0+', '0', input_string)


class Solution:
    def maxOperations(self, s: str) -> int:
        s = compress_zeros(s)
        cur = 0
        ans = 0
        for c in s[::-1]:
            if c == '0':
                cur += 1
            elif c == '1':
                ans += (cur)
        return ans
