class Solution {
public:
    int maxOperations(string s) {
          int on=0;
          int ans=0;
          for(int i=0;i<s.length()-1;i++){
            if(s[i]=='1'){
                on++;
            }
            if(s[i]== '1' && s[i+1]=='0'){
                ans+=on;
            }
          }
          return ans;
    }
};
