class Solution {
    public int maxOperations(String s) {
        int curr=0;
        int ans=0;
        int i =0;
        for(; i<s.length()-1; i++) {
            if(s.charAt(i)=='1') curr++;
            if(s.charAt(i)=='1' && s.charAt(i+1)=='0') {
                ans += curr;
            }
        }
        return ans;
    }
}
