class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt1 = 0;
        int ret = 0;
        for (int i = 0; i < n - 1; i++) {
            if (s[i] == '1' && s[i + 1] == '0') {
                ret++;
                ret += cnt1;
                cnt1++;
            } else if (s[i] == '1') {
                cnt1++;
            }
        }
        return ret;
    }
};
