func maxOperations(s string) (res int) {
    n := len(s)
    pre, cnt := -1, 0
    for i, c := range s {
        if i == n-1 {
            if c == '0' || (c == '1' && pre != i - 1) {
                res += cnt
            }
            break
        }
        if c == '1' && pre != i - 1 {
            res += cnt
        }
        if c == '1' {
            pre = i
            cnt++
        }
    }
    return res
}
