class Solution {
public:
    int maxOperations(string s) {
        int t=0;
        int sum=0;
        if(s[0]=='1'){
            t=1;
        }
        for(int i=1;i<s.size();i++){
            if(s[i]=='1' and s[i-1]=='0'){
                sum+=t;
                t++;
            }
            else if(s[i]=='1'){
                t++;
            }
        }
        if(s[s.size()-1]=='0'){
            sum+=t;
        }
        return sum;
    }
};
