class Solution:
    def maxOperations(self, s: str) -> int:
        # s = s + '1'
        holder =[]
        counter = 0
        for i in range(len(s) -1):

            if s[i] == "1" and s[i+1] == "1":
                counter +=1
            if s[i] == "1" and s[i+1] == "0":
                holder.append(counter + 1)
                counter = 0
        print(holder)
        res = 0
        for i in range(len(holder)):
            res += holder[i] * (len(holder)-i)
        return res
