class Solution {
    public int maxOperations(String s) {
        char[] strs = s.toCharArray();
        int count = 0;
        int ans = 0;

        for(int i = 0;i < strs.length - 1;i++){
            if(strs[i] == '1'){
                count++;
            }
            if(strs[i] == '1' && strs[i + 1] == '0'){
                ans += count;
            }
        }

        return ans;
    }
}
