class Solution {
public:
    int maxOperations(string s) {
       vector<int> v;
       v.push_back(s.size());
       for(int i=s.size()-1;i>=0;i--)
       {
            if(s[i]=='1')
            v.push_back(i);
       }
       int ans=0;
        int pre=0;
       for(int i=1;i<v.size();i++)
       {
            if(v[i-1]-v[i]>1)
            pre+=1;
           // cout<<pre<<endl;
            ans+= pre;
       }

        return ans;

    }
};
