class Solution {
public:
    int maxOperations(string s) {
        int ops=0, n = s.length(), ones=0, prev=0;
        for (int i=0; i<n; i++) {   
            if (s[i] == '1') ones++;
            else if (s[i] == '0' and ones>0) {
                prev += ones;
                ops += prev;                
                ones = 0;
            }                         
        }

        return ops;
    }
};
