class Solution:
    def maxOperations(self, s: str) -> int:
        cnt = 0
        ans = 0
        n = len(s)

        for i in range(n - 1):
            if s[i] == '1' and s[i + 1] == '1':
                cnt += 1
            elif s[i] == '1' and s[i + 1] == '0':
                cnt += 1
                ans += cnt

        return ans
