class Solution {
    public int maxOperations(String s) {
        int countOne = 0;

        int ans = 0;
        for(int i=0; i<s.length(); i++) {
            if(s.charAt(i)=='0') {
                if(i>0 && s.charAt(i-1)=='1') {
                    ans+=countOne;
                }
            }else {
                countOne++;
            }
        }

        return ans;
    }
}
