class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int i;
        int last = -1;
        int tot = 0;
        for (i = 0; i < n; i++) {
            if (s[i] == '1') {
                if (last != -1 && last != i - 1) {
                    ans += tot;
                }
                last = i;
                tot ++;
            }
        }
        if (s[n - 1] == '0') {
            ans += tot;
        } 
        return ans;
    }
};
