class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        bool flag = false;
        int answer = 0;
        for (int i = s.size() - 1; i >= 0; i--){
            if (s[i] == '0'){
                if (!flag) {
                    flag = true;
                    cnt++;
                }
            }
            else {
                flag = false;
                answer += cnt;
            }
        }
        return answer;
    }
};
