class Solution {
public:
    int maxOperations(string s) {
        int c=0; int ans=0;
        for(int i=0; i<s.size()-1; i++){
            if(s[i]=='1' && s[i+1] != '0') c++;
            if(s[i]=='1' && s[i+1]=='0'){
                c++;
                ans+=c;
            }
        }
        return ans;
    }
};
