class Solution:
    def maxOperations(self, s: str) -> int:
        ptr = 0
        n_ones = 0
        res = 0
        while ptr < len(s):
            while ptr < len(s) and s[ptr] == "0":
                ptr += 1

            while ptr < len(s) and s[ptr] == "1":
                n_ones += 1
                ptr += 1
            if ptr < len(s):
                res += n_ones
                ptr += 1
        return res
