class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        vector<int> indice;

        for(int i = 0;i < s.length();i++)
            if(s[i] == '1')
                indice.push_back(i);

        indice.push_back(s.length());

        for(int i = 0;i < indice.size() - 1;i++)
            if(indice[i + 1] - 1 != indice[i])
                res+= (i + 1);

        return res;
    }
};
