class Solution {
public:
    int maxOperations(string s) 
    {
        int n=s.size();
        int ct=0, ans=0;
        if(s[n-1]=='0') ct=1;
        for(int i=n-1;i>=0;i--)
        {
            if(s[i]=='0') continue;
            // if(s[i]=='1' && ct==0) continue;
            else 
            {
                while(i>=0 && s[i]=='1')
                {
                    ans+= ct;
                    i--;
                }
                ct++;
            }
        }
        return ans;
    }
};
