class Solution {
public:
    int maxOperations(string s) {
        int c=0,o=0,z=0;
        vector<int> pre;
        for(auto i:s){
            if(i=='1'){
                pre.push_back(1);
                o=1;
            }
            else if(i=='0' && o){
                pre.push_back(0);
                o=0;
            }
        }
        for(auto i:pre){
            if(i) z+=1;
            else c+=z;
        }
        return c;
    }
};
