class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, ans = 0;
        bool ok = 0;
        for (char c : s) {
            if (c == '1') {
                if (ok) {
                    ok = 0;
                    ans += cnt;
                }
                ++cnt;
            } else {
                ok = 1;
            }
        }
        if (ok) {
            ans += cnt;
        }
        return ans;
    }
};
