class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        // int id1 = -1;
        // for(int i = n - 1; i >= 0; --i){
        //     if(s[i] == '1'){
        //         id1 = i;
        //         break;
        //     }
        // }
        int id2 = -1;
        for(int i = n-1; i >= 0; --i){
            if(s[i] == '0'){
                id2 = i;
                break;
            }
        }
        if(id2 == -1)return 0;
        vector<char>v;
        for(int i = id2 - 1; i >= 0; --i){
            v.push_back(s[i]);
        }
        int ans = 0;
        int num = 1;
        char pre = '0';
        for(int i = 0; i < v.size(); ++i){
            if(v[i] == '1'){
                ans += num;
            }
            else{
                if(pre != v[i])++num;
            }
            pre = v[i];
        }
        return ans;
    }
};
