class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(),ct=0,ans=0;bool c=false;

        vector<int>a;
        if(s[0]=='0')a.push_back(0);
        if(s[0]=='1')a.push_back(1);
        for(int i=1;i<n;i++){
            if(s[i]=='0' && a.back()!=0)a.push_back(0);
            if(s[i]=='1')a.push_back(1);
        }
        for(int i=a.size()-1;i>=0;i--){
            cout<<a[i]<<" ";
            if(a[i]==0){
                ct++;
            }
            else{
                ans+=ct;
            }
        }//if(c)ct++;
        return ans;
    }
};
