class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int op=0;
        bool found1=false;
        int one_cnt=0,ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                found1=true;
                one_cnt++;
            }else{
                if(found1==true){
                ans+=one_cnt;
                found1=false;
                }
            }
        }
        return ans;
    }
};
