class Solution {
public:
    int maxOperations(string s) {

        int sol = 0;
        bool flag = 0;
        int cnt = 0;
        for(char ch :s){
            if(ch == '1'){
                cnt++;
                flag = 1;

            }else if(ch == '0' && flag == 1){
                   sol +=cnt;
                   flag = 0;
            }


        }
        return sol;
    }
};
