class Solution {
public:
    int maxOperations(string s) {
        int move  = 0;
        int ans = 0;
        for(int i = 0;i<s.size();i++){
           if(s[i] == '0'){
            if(i == s.size()-1 || s[i+1] == '1')
            ans += move;
           }
           else
           move++;
        }
        return ans;
    }
};
