class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
       int ans=0;
       string x;
       for(int i=0; i<n; i++){
        if(x.size()==0)x+=s[i];
         else  if(s[i]=='0'&&x[x.size()-1]=='0')continue;
         else x+=s[i];
       }
       cout<<x<<endl;
       int ct=0;
       for(int i=0; i<x.size(); i++){
        if(x[i]=='1')ct++;
        else ans+=ct;
       }
       return ans;
    }
};
