class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int now = 0,flag = 0;
        for(int i = s.size() - 1;i >= 0;i--){
            if(s[i] == '0'){
                if(flag == 0){
                    flag = 1;
                }
            }
            else{
                if(flag == 1){
                    flag = 0;
                    now++;
                    cnt += now;
                }
                else{
                    cnt += now;
                }
            }
        }

        return cnt;
    }
};
