class Solution {
public:
    int maxOperations(string s) {
        int len=s.length();
        int temp=0;int ans=0;
        for(int i=len-1;i>=0;i--){
            char ch=s[i];
            if(ch=='1' && i<len-1){
                if(s[i+1]=='0'){
                    temp++;
                    ans+=temp;

                }
                else if(s[i+1]=='1'){
                    temp=temp;
                    ans+=temp;
                }
            }
        }


        return ans;
    }
};
