import java.util.ArrayList;
import java.util.List;

class Solution {
    public int maxOperations(String s) {
        List<String> foodList = extractFood(s);
        return calculateOperations(foodList);
    }

    private List<String> extractFood(String s) {
        List<String> foodList = new ArrayList<>();
        boolean hasApple = false;

        for (char c : s.toCharArray()) {
            switch (c) {
                case '1':
                    foodList.add("Burger");
                    hasApple = true;
                    break;
                case '0':
                    if (hasApple) {
                        foodList.add("Pasta");
                        hasApple = false;
                    }
                    break;
            }
        }

        return foodList;
    }

    private int calculateOperations(List<String> foodList) {
        int operations = 0;
        int burgerCount = 0;

        for (String food : foodList) {
            switch (food) {
                case "Burger":
                    burgerCount++;
                    break;
                case "Pasta":
                    operations += burgerCount;
                    break;
            }
        }

        return operations;
    }
}
