class Solution:
    def maxOperations(self, s: str) -> int:
        s += '1'
        res = 0
        cnt1 = 0
        cnt0 = 0
        for i in range(len(s)):
            if s[i] == '1':
                res += cnt1 * cnt0
                cnt1 += 1
                cnt0 = 0
            else:
                cnt0 = 1
        return res
