class Solution {
public:
    int maxOperations(string s) {
        unordered_map<int, int> d;
        int i = s.length() - 1;
        while (i >= 0 && s[i] == '1') i--;
        while (i >= 0) {
            if (s[i] == '1') {
                d[i] = 1;
                int t = i;
                while (i > 0 && s[i - 1] == '1') d[t]++, i--;
            }
            i--;
        }
        vector<int> v;
        for (auto& p : d) v.push_back(p.first);
        sort(v.begin(), v.end());
        int a = 0;
        for (int j = v.size() - 1; j >= 0; --j) a += (v.size() - j) * d[v[j]];
        return a;
    }
};
