class Solution {
public:
    int maxOperations(string s) {
        string t = "" ;
        for(auto x : s){
            if(x == '0' && t.size() && t.back() == '1'){
                t += '0' ;
            }
            else if(x == '1'){
                t += '1' ;
            }   
        }
        long long ans = 0 , cnt = 0 ;
        for(auto x : t){
            if(x == '1'){
                cnt++ ;
            }
            else{
                ans += cnt ;
            }
        }
        return ans ;
    }
};
