class Solution {
public:
    int maxOperations(string s) {
        char prev='1';
        int count=0;
        int result=0;
        for(int i=0;i<s.size();i++)
        {
            if(prev=='0' && s[i]=='1') result+=count;
            if(s[i]=='1') count++;
            prev=s[i];
        }
        if(prev=='0')result+=count;
        return result;
    }
};
