import java.util.regex.*;

class Solution {
    public int maxOperations(String s) {
        Matcher m = Pattern.compile("1+0").matcher(s);
        int result = 0;
        int sum = 0;
        while(m.find()) {
            sum += m.group().length() - 1;
            result += sum;
        }
        return result;
    }
}
