class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        vector<int> prefix(n, 0);

        for(int i = 0; i < n; i++) 
            prefix[i] = (i > 0 ? prefix[i-1] : 0) + (s[i] == '1');


        int ans = 0;
        for(int i = n - 1; i >= 0; i--) {
            if(s[i] == '0' && (i == n - 1 || s[i+1] == '1')) {
                ans += prefix[i];
            }
        }

        return ans;
    }
};
