class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int count = 0;
        int f = 0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='0'){
                if(f==0){
                ans+=count;
                f=1;
                }

            }
            else{
                count++;
                f=0;
            }
        }
        return ans;
    }
};
