class Solution:
    def maxOperations(self, s: str) -> int:
        conone = 0
        point = 0
        answer = 0

        while point < len(s):

            while point < len(s) and s[point] != '1':
                point += 1

            while point < len(s) and s[point] != '0':
                point += 1
                conone += 1
            if point < len(s):
                answer += conone

        return answer
