impl Solution {
    pub fn max_operations(s: String) -> i32 {
        let mut num_ops = 0;
        let mut num_ones = 0;
        let mut has_zero = false;

        for ch in s.chars() {
            if ch == '1' {
                if has_zero {
                    num_ops += num_ones;
                    has_zero = false;
                }
                num_ones += 1;

            } else {
                has_zero = true;
            }
        }

        if has_zero {
            num_ops += num_ones;
        }

        num_ops
    }
}
