class Solution:
    def maxOperations(self, s: str) -> int:

        lens = len(s)
        s = s[::-1]
        res = 0
        cnt0 = 0

        for i,c in enumerate(s):
            if c == '1':
                res += cnt0
            else:
                if i == 0 or s[i-1] == '1':
                    cnt0 += 1

        return res
