class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int o = 0;
        bool f = true;
        for(int i = 0;i<s.length();i++){
            if(s[i]=='1'){
                o++;
                if(!f){
                    f = true;
                }
            }
            else{
                if(f){
                    res+=o;
                    f=false;
                }
            }
        }
        return res;
    }
};
