class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();

        int cnt=0;
        int i=0;
        while(i<n && s[i]=='0'){
            i++;
        }

        vector<int> ans;
        while(i<n){

            int cnt=0;
            while(i<n && s[i]=='1'){
                cnt++;
                i++;
            }

            while(i<n && s[i]=='0'){
                i++;
            }

            ans.push_back(cnt);
        }

        if(s[n-1]=='1'){
            ans.pop_back();
        }

        int fans=0,sum=0;
        for(auto i:ans){
            sum+=i;
            fans+=sum;
            cout<<i<<' ';
        }

        return fans;
    }
};
