class Solution {
    public int maxOperations(String s) {
        int res = 0;
        int num = 0;

        char[] cs = s.toCharArray();
        for (int i = 0; i < cs.length; i++) {
            if (cs[i] == '0' && (i == cs.length - 1 || cs[i + 1] == '1')) {
                res += num;
            } else if (cs[i] == '1') {
                num++;
            }
        }

        return res;
    }
}
