class Solution {
public:
    int maxOperations(string s) {
        int n=s.length();
        vector<int>v;
        for(int i=0;i<n;i++)if(s[i]=='1')v.push_back(i);
        v.push_back(n);
        int cnt=0;
        n=v.size();
        int ans=0;
        for(int i=0;i<n;) {
            ans+=i;
            int j=i+1;
            while(j<n and v[j]-v[j-1]==1)j++;
            i=j;
        }
        return ans;
    }
};
