class Solution {
public:
    int maxOperations(string s) {
        int ones = 0 ;
        int ans = 0 ;
        for(int i =0 ; i<s.length() ;i++){
            if(s[i]=='1') ones++;
            if(s[i]=='0') {
                if(s[i+1]=='1' or i+1==s.length()) ans+=ones;
            }
        }
        return ans ;
    }
};
