class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        vector<int> ans;
        int i = s.size() - 1; 
        while(i >= 0) {
            if(s[i] == '0') {
                cnt++;
                while(i >= 0 && s[i] == '0') i--;
            } 
            else {
                ans.push_back(cnt);
                i--;
            }    
        }
        return accumulate(ans.begin(), ans.end(), 0);
    }
};
