class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int has1 = 0;
        boolean oo = false;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                if (oo) ans += has1;
                has1++;
                oo = false;
            } else oo = true;
        }
        if (s.charAt(s.length() - 1) != '1') ans += has1;
        return ans;
    }
}
