class Solution {
public:
    int maxOperations(string s) {
        string ss;
        for (auto c : s) {
            if (c == '0') {
                if (!ss.empty() and ss.back() == '0') continue;
                ss.push_back(c);
            }
            else ss.push_back(c);
        }
        int n = ss.size(), res = 0, cnt = 0;
        for (int i = n - 1; i >= 0; i --) {
            if (ss[i] == '0') cnt += 1;
            else res += cnt;
        }
        return res;
    }
};
